import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const JoinChatPage: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  // CreateChatPageから渡されたstateがあれば初期値として利用
  const state = (location.state as { sessionId?: string; pin?: string }) || {};
  const [chatId, setChatId] = useState(state.sessionId || '');
  const [pin, setPin] = useState(state.pin || '');
  const [userName, setUserName] = useState('');
  const [error, setError] = useState('');

  const handleJoin = async () => {
    if (!chatId || !pin || !userName) {
      setError('すべての項目を入力してください');
      return;
    }

    // chatIdをそのままsessionIdとして利用する
    const sessionId = chatId;

    // joinSession APIを呼び出す
    const response = await fetch('http://localhost:3000/api/joinSession', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ sessionId, pin, userName })
    });

    if (response.ok) {
      const data = await response.json();
      // APIから返ってくるsessionオブジェクトにはroomNameが含まれている前提
      const roomName = data.session.roomName;
      navigate(`/chat/${sessionId}`, { state: { userName, roomName } });
    } else {
      const data = await response.json();
      setError(data.error || '参加に失敗しました');
    }
  };

  return (
    <div style={{ backgroundColor: 'black', color: 'white', height: '100vh', padding: '20px' }}>
      <h2>チャット参加</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <input
        type="text"
        placeholder="チャットIDを入力"
        value={chatId}
        onChange={(e) => setChatId(e.target.value)}
        style={{
          backgroundColor: 'gray',
          color: 'white',
          padding: '10px',
          marginBottom: '10px',
          display: 'block',
          width: '100%'
        }}
      />
      <input
        type="text"
        placeholder="PINを入力"
        value={pin}
        onChange={(e) => setPin(e.target.value)}
        style={{
          backgroundColor: 'gray',
          color: 'white',
          padding: '10px',
          marginBottom: '10px',
          display: 'block',
          width: '100%'
        }}
      />
      <input
        type="text"
        placeholder="ユーザー名を入力"
        value={userName}
        onChange={(e) => setUserName(e.target.value)}
        style={{
          backgroundColor: 'gray',
          color: 'white',
          padding: '10px',
          marginBottom: '10px',
          display: 'block',
          width: '100%'
        }}
      />
      <button
        onClick={handleJoin}
        style={{
          backgroundColor: 'gray',
          color: 'white',
          padding: '10px',
          width: '100%'
        }}
      >
        参加
      </button>
    </div>
  );
};

export default JoinChatPage;
