// frontend/src/pages/ChatPage.tsx
import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import io, { Socket } from 'socket.io-client';

interface ChatMessage {
  id: string;
  sender: string;
  content: string;
  timestamp: number;
  type: 'text' | 'file' | 'image' | 'video';
}

const ChatPage: React.FC = () => {
  const { sessionId } = useParams<{ sessionId: string }>();
  const location = useLocation();
  const { userName, roomName, pin } = (location.state as {
    userName: string;
    roomName: string;
    pin: string;
  }) || { userName: '', roomName: '', pin: '' };
  const [socket, setSocket] = useState<Socket | null>(null);
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const newSocket = io('http://localhost:3000');
    setSocket(newSocket);

    newSocket.on('connect', () => {
      newSocket.emit('joinRoom', { sessionId, userName });
    });

    newSocket.on('chatHistory', (chatHistory: ChatMessage[]) => {
      setMessages(chatHistory);
    });

    newSocket.on('newMessage', (message: ChatMessage) => {
      setMessages((prev) => [...prev, message]);
    });

    newSocket.on('errorMessage', (msg: string) => {
      setError(msg);
    });

    return () => {
      newSocket.disconnect();
    };
  }, [sessionId, userName]);

  const handleSend = () => {
    if (socket && newMessage.trim() !== '') {
      socket.emit('chatMessage', { content: newMessage, type: 'text' });
      setNewMessage('');
    }
  };

  // Enter キーで送信する処理
  const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Enter') {
      e.preventDefault();
      handleSend();
    }
  };

  return (
    <div className="container">
      <section className="chat-screen animate-in">
        <div className="chat-header">
          <div className="chat-info">
            <div className="room-name">チャットルーム: {roomName}</div>
            <div className="user-info">あなたのユーザーID: {userName}</div>
            <div className="session-info">
              セッションID: {sessionId} / PIN: {pin}
            </div>
          </div>
        </div>
        <div className="messages-container">
          {error && <p style={{ color: 'red' }}>{error}</p>}
          {messages.map((msg) => (
            <div key={msg.id} className="message">
              <div className="message-header">
                <span className="time">
                  {new Date(msg.timestamp).toLocaleTimeString()}
                </span>
                <span className="user">{msg.sender}</span>
              </div>
              <div className="message-content">{msg.content}</div>
            </div>
          ))}
        </div>
        <div className="input-area">
          <input
            type="text"
            className="message-input"
            placeholder="メッセージを入力..."
            value={newMessage}
            onChange={(e) => setNewMessage(e.target.value)}
            onKeyDown={handleKeyDown}  // Enterキー押下時の処理を追加
          />
          <button className="send-btn" onClick={handleSend}>
            送信
          </button>
        </div>
      </section>
    </div>
  );
};

export default ChatPage;
