// frontend/src/pages/CreateChatPage.tsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const CreateChatPage: React.FC = () => {
  const navigate = useNavigate();
  const [roomName, setRoomName] = useState('');
  const [chatType, setChatType] = useState<'individual' | 'group'>('individual');
  const [error, setError] = useState('');

  const handleCreate = async () => {
    if (!roomName) {
      setError('ルーム名を入力してください');
      return;
    }

    // チャットセッション作成APIを呼び出す
    const response = await fetch('http://localhost:3000/api/createSession', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ roomName, type: chatType })
    });

    if (response.ok) {
      const data = await response.json();
      // セッションIDとPINをlocalStorageに保存
      localStorage.setItem('sessionId', data.sessionId);
      localStorage.setItem('pin', data.pin);
      // 自動的に参加画面へ遷移
      navigate('/join');
    } else {
      const data = await response.json();
      setError(data.error || 'チャット作成に失敗しました');
    }
  };

  return (
    <div style={{ backgroundColor: 'black', color: 'white', height: '100vh', padding: '20px' }}>
      <h2>チャット作成</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <input
        type="text"
        placeholder="ルーム名を入力"
        value={roomName}
        onChange={(e) => setRoomName(e.target.value)}
        style={{
          backgroundColor: 'gray',
          color: 'white',
          padding: '10px',
          marginBottom: '10px',
          display: 'block',
          width: '100%'
        }}
      />
      <div>
        <button
          onClick={() => setChatType('individual')}
          style={{ backgroundColor: 'gray', color: 'white', marginRight: '10px' }}
        >
          個人チャット
        </button>
        <button
          onClick={() => setChatType('group')}
          style={{ backgroundColor: 'gray', color: 'white' }}
        >
          グループチャット
        </button>
      </div>
      <button
        onClick={handleCreate}
        style={{ backgroundColor: 'gray', color: 'white', marginTop: '20px' }}
      >
        作成
      </button>
    </div>
  );
};

export default CreateChatPage;
