import React, { useEffect, useState } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import io, { Socket } from 'socket.io-client';

interface ChatMessage {
  id: string;
  sender: string;
  content: string;
  timestamp: number;
  type: 'text' | 'file' | 'image' | 'video';
}

const ChatPage: React.FC = () => {
  const { sessionId } = useParams<{ sessionId: string }>();
  const location = useLocation();
  const { userName, roomName } = location.state as { userName: string; roomName: string };
  const [socket, setSocket] = useState<Socket | null>(null);
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [newMessage, setNewMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    // バックエンドの Socket.IO サーバーへ接続
    const newSocket = io('http://localhost:3000');
    setSocket(newSocket);

    newSocket.on('connect', () => {
      console.log('Socket接続完了');
      newSocket.emit('joinRoom', { sessionId, userName });
    });

    newSocket.on('chatHistory', (chatHistory: ChatMessage[]) => {
      setMessages(chatHistory);
    });

    newSocket.on('newMessage', (message: ChatMessage) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    newSocket.on('errorMessage', (msg: string) => {
      setError(msg);
    });

    return () => {
      newSocket.disconnect();
    };
  }, [sessionId, userName]);

  const handleSend = () => {
    if (socket && newMessage.trim() !== '') {
      socket.emit('chatMessage', { content: newMessage, type: 'text' });
      setNewMessage('');
    }
  };

  return (
    <div
      style={{
        backgroundColor: 'black',
        color: 'white',
        height: '100vh',
        display: 'flex',
        flexDirection: 'column'
      }}
    >
      <header style={{ padding: '10px', borderBottom: '1px solid gray' }}>
        <h2>チャットルーム {roomName}</h2>
        <p>あなたのユーザーID: {userName}</p>
      </header>
      <main style={{ flex: 1, overflowY: 'auto', padding: '10px' }}>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {messages.map((message) => (
          <div key={message.id} style={{ marginBottom: '10px' }}>
            <span>
              {new Date(message.timestamp).toLocaleTimeString()} - {message.sender}:
            </span>
            <p>{message.content}</p>
          </div>
        ))}
      </main>
      <footer style={{ padding: '10px', borderTop: '1px solid gray', display: 'flex' }}>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="メッセージを入力..."
          style={{
            backgroundColor: 'gray',
            color: 'white',
            padding: '10px',
            flex: 1
          }}
        />
        <button
          onClick={handleSend}
          style={{
            backgroundColor: 'gray',
            color: 'white',
            padding: '10px',
            marginLeft: '10px'
          }}
        >
          送信
        </button>
      </footer>
    </div>
  );
};

export default ChatPage;
